function  generateProposalsForImages()
cd('/srv/share/objectProposals/rantalankilaSegments/');
addpath(genpath('/srv/share/objectProposals/dependencies/'));
addpath(genpath(pwd));
VLFEAT='/srv/share/objectProposals/dependencies/vlfeat-0.9.16/';

run([VLFEAT 'toolbox/vl_setup']);

config=createConfig();
imageLoc=config.path.input;
outputLoc=config.path.output;
%extension inclues .
imageExt=config.opts.imageExt;
images=dir([imageLoc '*' imageExt]);
%use default options generated by code

spagglom_options;

for i=1:length(images)
        imName=images(i).name;
        image=imread([imageLoc imName]);
        
	if(~size(image,3)==3)
		image=repmat(image,[1 1 3]);
	end
        %andidates stores bounding boxes too..
	[region_parts, orig_sp]= spagglom(image,opts);
	
	
        proposalFileName=strrep(imName,imageExt,'.mat');
        save([outputLoc proposalFileName],'region_parts','orig_sp');
end


